import React, { Component } from "react";
import { Navbar, Nav, Form, FormControl } from "react-bootstrap";
import { Button } from "primereact/button";
import { InputText } from "primereact/inputtext";
import { connect } from "react-redux";
import { storeData } from "../redux";
import YearRange from "./YearRange";
import Icon from 'react-native-vector-icons/FontAwesome';


class MyNavbar extends Component {
  constructor(storeData) {
    super();
    this.state = {
      title: "Avengers",
      ismounted: false,
    };
  }
  componentDidMount = () => {
    this.setState({
      ismounted: true,
    });
  };
  handleSearch = () => {
    this.props.storeData({ varName: "title", data: this.state.title });
  };
  render() {
    return (
      <div>
        {this.state.ismounted ? (
          <Navbar>
            <Navbar.Brand style = {{color: "whitesmoke"}} className = "navbar" >
              <h1>SEARCHFLIX</h1>
            </Navbar.Brand>

            <div inline style = {{display: "flex",justifyContent: "center"}} >
            <Form style = {{paddingRight : "5%",display: "flex",justifyContent: "center"}}>
                <InputText
                className="form-control mr-sm-2"
                  value={this.state.title}
                  onChange={(e) => {
                  this.setState({ title: e.target.value });
                  }}
                />
            </Form>
            <Button style={{fontSize: "80%", justifyContent:"center",backgroundColor: "#004180",}} label="Search " onClick={this.handleSearch} />
            </div>
          </Navbar>

        ) : (
          ""
        )}
        <div inline style = {{display: "flex",paddingLeft:"2%"}}>
              <YearRange/>
        </div>
        <Button
  icon={
    <Icon
      name="arrow-right"
      size={15}
      color="white"
    />
  }
  iconRight
  title="Button with right icon"
/>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {};
};

const mapDispatchToProps = (dispatch) => {
  return {
    storeData: (data) => dispatch(storeData(data)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(MyNavbar);
