import React, { Component } from "react";
import { Navbar, Nav, Form, FormControl } from "react-bootstrap";
import { Button } from "primereact/button";
import { InputText } from "primereact/inputtext";
import { connect } from "react-redux";
import { storeData } from "../redux";
import YearRange from "./YearRange";


class MyNavbar extends Component {
  constructor(storeData) {
    super();
    this.state = {
      ismounted: false,
    };
  }
  componentDidMount = () => {
    this.setState({
      ismounted: true,
    });
  };
  handleSearch = () => {
    this.props.storeData({ varName: "title", data: this.state.title });
  };
  render() {
    return (
      <div>
        {this.state.ismounted ? (
          <Navbar>
            <Navbar.Brand style = {{color: "whitesmoke",alignContent:"center"}} className = "navbar" >
              <h1>SEARCHFLIX</h1>
            </Navbar.Brand>



          </Navbar>

        ) : (
          ""
        )}
        <div inline style = {{display: "flex",justifyContent: "center"}} >
            <Form style = {{paddingRight : "2%",display: "flex",justifyContent: "center"}}>
                <InputText
                  value={this.state.title}
                  placeholder="Lookup a movie..."
                  onChange={(e) => {
                  this.setState({ title: e.target.value });
                  }}
                />
            </Form>
            <YearRange/>
            <Button style={{marginLeft: "2%",fontSize: "80%", justifyContent:"center",backgroundColor: "#004180"}} label="Search " onClick={this.handleSearch} onKeyDown={this.handleSearch} />
            </div>

      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {};
};

const mapDispatchToProps = (dispatch) => {
  return {
    storeData: (data) => dispatch(storeData(data)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(MyNavbar);
