import React, { Component } from "react";
import { Navbar, Nav, Form, FormControl } from "react-bootstrap";
import { Button } from "primereact/button";
import { InputText } from "primereact/inputtext";
import { connect } from "react-redux";
import { storeData } from "../redux";

class MyNavbar extends Component {
  constructor(storeData) {
    super();
    this.state = {
      title: "Avengers",
      ismounted: false,
    };
  }
  componentDidMount = () => {
    this.setState({
      ismounted: true,
    });
  };
  handleSearch = () => {
    this.props.storeData({ varName: "title", data: this.state.title });
  };
  render() {
    return (
      <div>
        {this.state.ismounted ? (
          <Navbar>
            <Navbar.Brand style = {{color: "whitesmoke"}} className = "navbar" >
              <h1>SEARCHFLIX</h1>
            </Navbar.Brand>
            <div inline className="md-form mr-auto mb-6" style = {{display: "inline-flex"}} >
            <Form style = {{display: "inline-flex"}}>
                <InputText 
                className="form-control mr-sm-2"
                  value={this.state.title}
                  onChange={(e) => {
                  this.setState({ title: e.target.value });
                  }}
                />
            </Form>
            <Button style={{backgroundColor: "#004180"}} label="Search " onClick={this.handleSearch} />
            </div>
          </Navbar>

        ) : (
          ""
        )}

      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {};
};

const mapDispatchToProps = (dispatch) => {
  return {
    storeData: (data) => dispatch(storeData(data)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(MyNavbar);
